or=#225486><font color='#ffffff'><b>EN LA FACTURA</b></td></tr>";		/*Busco los Valores de LAs Facturas correspondientes a las Fechas*/		$query = "select calidad_000001.Valor,calidad_000001.Fecha from calidad_000001 where calidad_000001.fecha between '".$fecha1."' and  '".$fecha2."' order by calidad_000001.Fecha";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		$sum=0;		for ($i=0;$i<$num;$i++)		{			$row = mysql_fetch_array($err);			$num1=explode('.',$row[0]);			$num1[0]=str_replace(',','',$num1[0]);			$sum=$sum+$num1[0];		}		echo "<tr><td colspan='2'bgcolor=#A4E1E8><b>Valor Total de las Facturas Auditadas:</b></td><td bgcolor=#A4E1E8><B>".number_format($sum,2,'.',',')."</TD></tr>";		echo "<tr><td colspan='2'bgcolor=#A4E1E8><b>Valor Promedio De las Facturas Auditadas:</b></td><td bgcolor=#A4E1E8><B>".number_format($sum/$num,2,'.',',')."<br>";		/*Busco en las Inconsistencias de la Factura, 		para las Facturas entre las dos Fechas y las desplegamos por el tipo de ota que generan,		Cada tipo de nota con su nunero de incidencias y su valor totalizado*/		$query = "select COUNT(calidad_000003.Tipo_nota), calidad_000003.Tipo_nota,Sum(calidad_000003.Valor_nota) from calidad_000003, calidad_000001 where  calidad_000001.fecha between '".$fecha1."' and  '".$fecha2."'and calidad_000003.factura=calidad_000001.factura group by calidad_000003.Tipo_nota ";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		echo "<tr>";		echo "<td bgcolor=#57C8D5><b>Cant.  de Notas</b></td>";		echo "<td bgcolor=#57C8D5><b>Tipo Nota</b></td>";		echo "<td bgcolor=#57C8D5><b>Valor total Notas</b></td>";		echo "</tr>";		$sum_inc=0;		for ($i=0;$i<$num;$i++)		{			$row = mysql_fetch_array($err);			echo "<tr>";			echo "<td align='center'><font color='#0055A8'><i>".$row[0]."</i></td>";// Numero De Notas de Ese tipo			echo "<td><font color='#0055A8'><i>".ucwords(strtolower(substr($row[1],3)))."</i></td>";//Nombre del Tipo de Nota			echo "<td>