ia clínica diferente de cero \n\r';			document.forms.forma.whistoria.value = '';			valido = false;		}		}		if(!valido)	{		alert("Error: \n\r" + mensaje);		return false;	} 	else 	{		if(confirm("Desea ingresar el paciente a urgencias?"))		{			ingresarPacienteUrg();		}		else		{			return false;		}	}}//Validar la Agenda Multiple para limpiar los campos 'Medico y Conducta' y cargar segundo registro.function asignarAgendaMultiple(i){	    	jConfirm("Realmente desea generar Agenda Multiple?","Confirmacion");	$( "#modal-btn-si" ).off("click").on("click",function(){	       var parametros = "consultaAjax=17&basedatos="+document.forms.forma.wbasedato.value+"&basedatoshce="+document.forms.forma.wbasedatohce.value+"&cco="+document.forms.forma.cco.value+"&paciente=" +document.getElementById('wpaciente'+i).value+"&ingreso=" +document.getElementById('wingreso'+i).value+"&wemp_pmla="+document.forms.forma.wemp_pmla.value+"&wserviciourl="+document.forms.forma.wserviciourl.value+"&seguridad=" +document.getElementById('wseguridad').value; 						$.post("agenda_ambulatorias.php", parametros,	 		      function(data){	                    if  (data.error == true){	 		        	    jAlert(data.message,'Mensaje');	 		        	    document.getElementById('redirec'+i).checked = false;	                    }	 		        	else{	 		        		$( "#wmedico"+i ).val('0');							$( "#wmedico"+i ).attr('disabled',false);							$( "#wconducta"+i ).val(' -- sin asignar -- ');							agendaUrgencias();	 		        	}	 		        	$( "#jConfirm" ).modal("hide");	 		 }, 'json');	});	$( "#modal-btn-no" ).off("click").on("click",function(){		document.getElementById('redirec'+i).checked = false;		$( "#jConfirm" ).modal("hide");	});}// Función para abrir en modal mediante iframe la venta de nueva admisiónfunction abrirVentanaAdmision(){		$("#modalAdmision").modal({ backdrop: 'static',keyboard: false});	$("#ifr_admision").contents().find("body").css("wid