  	if ($num1 > 0 )  	{  		for ($i=1;$i<=$num1;$i++)  		{  			$med = new medicosUrgencias();  			$row1 = mysql_fetch_array($res1);  			$med->codigo = $row1[0];  			$med->nombre = $row1[1]." ".$row1[2]." ".$row1[3]." ".$row1[4];  			$med->cedula = $row1[5];  			  			$coleccion[] = $med;  		}  	}  	return $coleccion;}/*********************************************************************************************VERIFICA SI LA HISTORIA DEL PACIENTE SE ENCUENTRA REGISTRADA EN URGENCIAS DE DB UNIX		*********************************************************************************************/function consultarPacienteUnix($pacienteConsulta,$servicio){	global $conexUnix;	$paciente = new pacienteDTO();		$q = " SELECT pacnom, pacap1, pacap2, pacnum, pacfec, pachor, pachab, paccer, pacres, pactid, pacced, pacnac, pacsex, pachos, serccocco, pactel, pacdir, pacmun		     FROM inpac, insercco		    WHERE pachis = '".$pacienteConsulta->historiaClinica."' 			  AND serccoser = pacser			  AND pacap2 is not null			  AND pachab is not null						UNION						SELECT pacnom, pacap1, ' ' AS pacap2, pacnum, pacfec, pachor, pachab, paccer, pacres, pactid, pacced, pacnac, pacsex, pachos, serccocco, pactel, pacdir, pacmun		     FROM inpac, insercco		    WHERE pachis = '".$pacienteConsulta->historiaClinica."' 			  AND serccoser = pacser			  AND pacap2 is null			  AND pachab is not null			  			UNION		   SELECT pacnom, pacap1, pacap2, pacnum, pacfec, pachor, ' ', paccer, pacres, pactid, pacced, pacnac, pacsex, pachos, serccocco, pactel, pacdir, pacmun		     FROM inpac, insercco		    WHERE pachis = '".$pacienteConsulta->historiaClinica."' 			  AND serccoser = pacser			  AND pacap2 is not null			  AND pachab is null						UNION						SELECT pacnom, pacap1, ' ' AS pacap2, pacnum, pacfec, pachor, ' ', paccer, pacres, pactid, pacced, pacnac, pacsex, pachos, serccocco, pactel, pacdir, pacmun		     FROM inpac, insercco		    WHERE pachis = '".$pacienteConsulta->historiaClinica."' 			  AND serccoser = pacser			  AND pacap2 is null			  AND pachab is null			  ";	$rs = odbc_do($conexUnix,$q) or die (odbc_errormsg());	//$campos = odbc_num_fields($rs);	if ($arr_paciente = odbc_fetch_array($rs))	{		$municipio = "";		$tipo_responsable = "";		//Busco el nombre del municipio		$muncod = trim($arr_paciente['pacmun']);		$qmun = "  SELECT munnom					 FROM inmun					WHERE muncod = '".trim($muncod)."'				  ";		$rsmun = odbc_do($conexUnix,$qmun) or die (odbc_errormsg());		$arr_municipio = odbc_fetch_array($rsmun);		$municipio = $arr_municipio['munnom'];		// $numreg = odbc_num_rows($rsmun);		// if($numemp>0)			// $municipio = trim(odbc_result($rsmun,1));		// else			// $municipio = "";		// Busco el tipo de responsable		$codResponsable = trim($arr_paciente['paccer']);		$qemp = "  SELECT emptip					 FROM inemp					WHERE empcod = '".$codResponsable."'					  AND emptip is not null				  ";		$rsemp = odbc_do($conexUnix,$qemp) or die (odbc_errormsg());		$arr_responsable = odbc_fetch_array($rsemp);		$tipo_responsable = $arr_responsable['emptip'];		// $numreg = odbc_num_rows($rsemp);		// if($numreg>0)			// $tipo_responsable = trim(odbc_result($rsemp,1));		// else			// $tipo_responsable = "";		$nombre = explode(" ",trim($arr_paciente['pacnom']));		$paciente->nombre1 = $nombre[0];		if(isset($nombre[1]) && !isset($nombre[2]))		{			$paciente->nombre2 = $nombre[1];		}		elseif(isset($nombre[1]) && isset($nombre[2]))		{			$paciente->nombre2 = $nombre[1]." ".$nombre[2];		}		elseif(!isset($nombre[1]) && isset($nombre[2]))		{			$paciente->nombre2 = $nombre[2];		}		else		{			$paciente->nombre2 = "";		}		$paciente->apellido1 = trim($arr_paciente['pacap1']);		$paciente->apellido2 = trim($arr_paciente['pacap2']);		$paciente->historiaClinica = trim($pacienteConsulta->historiaClinica);		$paciente->ingresoHistoriaClinica = trim($arr_paciente['pacnum']);		$paciente->fechaIngreso = str_replace("/","-",trim($arr_paciente['pacfec']));		$paciente->horaIngreso = str_replace(".",":",trim($arr_paciente['pachor'])).":00";		$paciente->habitacionActual = "";		$paciente->numeroIdentificacionResponsable = $codResponsable;		$paciente->nombreResponsable = trim($arr_paciente['pacres']);		$paciente->tipoDocumentoIdentidad = trim($arr_paciente['pactid']);		$paciente->documentoIdentidad = trim($arr_paciente['pacced']);		$paciente->fechaNacimiento = trim($arr_paciente['pacnac']);		$paciente->genero = trim($arr_paciente['pacsex']);		$paciente->deHospitalizacion = trim($arr_paciente['pachos']);		$paciente->servicioActual = trim($arr_paciente['serccocco']);		$paciente->tipoResponsable = $tipo_responsable;		$paciente->telefono = trim($arr_paciente['pactel']);		$paciente->direccion = trim($arr_paciente['pacdir']);		$paciente->municipio = $municipio;		if(!isset($paciente->tipoResponsable))		{			$paciente->tipoResponsable = "02";		}		else		{			if($paciente->tipoResponsable == '' || empty($paciente->tipoResponsable))			{				$paciente->tipoResponsable = "02";			}		}	}	return $paciente;}// 2013-02-08// Consulta los datos de una fila según el query $qlog y convierte esta fila en un String// separando cada campo por el caracter |function obtenerRegistrosFila($qlog){	global $conex;	$reslog = mysql_query($qlog, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qlog . " - " . mysql_error());	$rowlog = mysql_fetch_row($reslog);	$datosFila = implode("|", $rowlog);	return $datosFila;}/********************************************************************************************* FUNCIONES UTILIZADAS EN EL REGISTRO DEL PACIENTE QUE ENTRA A URGENCIAS					*********************************************************************************************///Existe un registro del paciente en la tabla 36 de rootfunction existeEnTablaUnicaPacientes($paciente){	global $conex; 		$esta = false;		$q = "SELECT 				* 		  	FROM			  		ro