o."'  				AND Ccocir   = 'on' 			";		$err = mysql_query($q,$conex);	$num = mysql_num_rows($err);		if($num>0)	{		$es = true;	} 		return $es;}/********************************************************************************************//********************************************************************************************* **************************	FUNCIONES DE LLAMADO AJAX ************************************* ********************************************************************************************/// Asigna el médico tratante a un paciente en urgenciasfunction asignarMedicoUrgencias($basedatos,$basedatoshce,$medico,$paciente,$ingreso,$servicio,$seguridad,$tipo_asignacion,$wserviciourl){	global $conex; 	global $wprounificado;		$q = "	SELECT Mtrhis 			FROM ".$basedatoshce."_000022 			WHERE Mtrhis = '".$paciente."' 			AND Mtring = '".$ingreso."'";		$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());	$num = mysql_num_rows($res);	// Consulto la especialidad del médico	$qesp = "	SELECT Medesp  				FROM ".$basedatos."_000048 				WHERE Meduma = '".$medico."' ";	$resesp = mysql_query($qesp, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qesp . " - " . mysql_error());	$rowesp = mysql_fetch_array($resesp);	$especialidad = explode("-",$rowesp['Medesp']);		$fecha = date("Y-m-d");	$hora = date("H:i:s");		if($num==0) 	{		$q = "INSERT INTO 				".$basedatoshce."_000022 					(Medico,Mtrfam,Mtrham,Mtrhis,Mtring,Mtrmed,Mtrest,Mtrtra,Mtretr,Mtreme,Mtrcci,Mtrcur,Seguridad)				VALUES 					('".$basedatoshce."','".$fecha."','".$hora."','".$paciente."','".$ingreso."','".$medico."','on','on','".$especialidad[0]."','".$especialidad[0]."','".$servicio."','off','C-".$seguridad."')";						$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				if($res)			return "ok";		else			return "No se pudo realizar la asignación. \n Error: ".$res;	}else {				//Si el update es por especialidad, solo se actualiza la especialidad que viene en la variable $medico.		if($tipo_asignacion == 'especialidad'){						$filtro = "Mtretr='".$medico."', 					   Mtreme='".$medico."',";					}else{						$filtro = "Mtrmed='".$medico."',					   					   Mtretr='".$especialidad[0]."', 					   Mtreme='".$especialidad[0]."',";		}        		if (strpos($wprounificado,$wserviciourl) !== false){            $q = "	UPDATE ".$basedatoshce."_000022 					   SET Mtrfam='".$fecha."', 						   Mtrham='".$hora."', 						   $filtro						   Mtrest='on', 						   Mtrtra='on',						   Mtrcur='off'					 WHERE Mtrhis = '".$paciente."'					   AND Mtring = '".$ingreso."'					   AND (Mtrmed = '' or  Mtrcon='') ";		}else{					$q = "	UPDATE ".$basedatoshce."_000022 					   SET Mtrfam='".$fecha."', 						   Mtrham='".$hora."', 						   $filtro						   Mtrest='on', 						   Mtrtra='on',						   Mtrcur='off'					 WHERE Mtrhis = '".$paciente."'					   AND Mtring = '".$ingreso."'";		}				$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				if($res)			return "ok";		else			return "No se pudo realizar la asignación. \n Error: ".$res;	}}// Borra los ingresos de una historia mayores al ingreso actual de Unixfunction borraIngresosMayores($basedatos,$basedatoshce,$codcco,$paciente,$ingreso,$wemp_pmla,$seguridad,$bandera,$fechaIngresoUnix,$horaIngresoUnix){	global $conex; 	global $conexUnix; 		$fecha = date("Y-m-d");	$hora = date("H:i:s");	//Consulto los ingresos mayores al ingreso actual en unix	$qmay = "	SELECT Fecha_data, Hora_data, Ubiing, Ubisac 				FROM ".$basedatos."_000018 				WHERE Ubihis = '".$paciente."' 				AND   Ubiing*1 >= ".$ingreso." ";	$resmay = mysql_query($qmay, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qmay . " - " . mysql_error());	$nummay = mysql_num_rows($resmay);		$ingreso_unix = $ingreso;	// Si encontró ingresos comience el borrado	if($nummay>0)	{	  while($rowmay = mysql_fetch_array($resmay))	  {		$ingreso = $rowmay['Ubiing'];		$fechaIngreso = $rowmay['Fecha_data'];		$horaIngreso = $rowmay['Hora_data'];		$ccoActual = $rowmay['Ubisac'];				// 2013-02-18		// Cosulto si el paciente tiene registros de movimiento hospitalario		$qeyr = " SELECT Eyrhis					FROM ".$basedatos."_000017				   WHERE Eyrhis = '".$paciente."'					 AND Eyring = '".$ingreso."'					 AND Eyrest = 'on'";		$reseyr = mysql_query($qeyr, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qeyr . " - " . mysql_error());		$numeyr = mysql_num_rows($reseyr);		// Si el paciente no ha tenido movimiento hospitalario si se puede hacer el borrado		if($numeyr==0)		{			$numant=0;			if($ingreso==$ingreso_unix)			{				// Traigo la hora de unix menos 10 minutos para comprar que la de matrix no sea menor				$horaUnix = date( "Y-m-d H:i:s", strtotime( $fechaIngresoUnix." ".$horaIngresoUnix ) - 10*60 );				$fecha = explode(" ",$horaUnix);				$fechaIngresoUnix = $fecha[0];				$horaIngresoUnix = $fecha[1];								// Valida si Fecha y hora registrados en Matrix son anteriores que los registrados en Unix				// Si es menor quiere deir que el ingreso fue reasignado en Unix pero no se borró de Matrix				// Entonces se debe borrar				//Consulto si tiene fecha hora de ingreso anterior a la de unix				$qant = "	SELECT Fecha_data							FROM ".$basedatos."_000018 							WHERE Ubihis = '".$paciente."' 							AND   Ubiing = '".$ingreso."' 							AND   ( 									(  Fecha_data < '".$fechaIngresoUnix."')									OR									(  Fecha_data = '".$fechaIngresoUnix."'									   AND Hora_data < '".$horaIngresoUnix."'									)								   ) ";				$resant = mysql_query($qant, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qant . " - " . mysql_error());				$numant = mysql_num_rows($resant);			}						if($ingreso!=$ingreso_unix || $numant>0)			{				$fechaLog = date('Y-m-d');				$horaLog = date('H:i:s');								//Borro registro en tabla 16 de Movhos 				$q = "	DELETE 						  FROM ".$basedatos."_000016 						 WHERE Inghis = '".$paciente."'						   AND Inging = '".$ingreso."'";				$res2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				$num_affect = mysql_affected_rows();				if($num_affect>0)				{					//Guardo LOG de borrado en tabla Movhos 16					$q = "	INSERT INTO log_agenda 											  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 									   VALUES											  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla movhos_000016', '".$seguridad."', '".$bandera."')";					$resl1 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				}				//Borro registro en tabla 18 de Movhos 				$q = "	DELETE 						  FROM ".$basedatos."_000018 						 WHERE Ubihis = '".$paciente."'						   AND Ubiing = '".$ingreso."'";				$res1 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				$num_affect = mysql_affected_rows();				if($num_affect>0)				{					//Guardo LOG de borrado en tabla Movhos 18 					$q = "	INSERT INTO log_agenda 											  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 									   VALUES											  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla movhos_000018', '".$seguridad."', '".$bandera."')";					$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				}				// Cosulto si el paciente ya está registrado en la tabla 22 de Hce				$q = "	SELECT Mtrhis 						FROM ".$basedatoshce."_000022 						WHERE Mtrhis = '".$paciente."' 						AND Mtring = '".$ingreso."'";				$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());				$num = mysql_num_rows($res);				if($num>0) 				{					$q = "	DELETE							  FROM ".$basedatoshce."_000022 							 WHERE Mtrhis = '".$paciente."'							   AND	Mtring = '".$ingreso."'";					$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());										$num_affect = mysql_affected_rows();					if($num_affect>0)					{						//Guardo LOG de borrado en tabla hce 22 						$q = "	INSERT INTO log_agenda 												  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 										   VALUES												  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla hce_000022', '".$seguridad."', '".$bandera."')";						$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());					}				}				// 2013-02-26				// Con esta función se borra el registro de egreso por alta que exista en la tabla 000033 de movimiento hospitalario				BorrarAltasMuertesAntesDeAgregarNueva($conex, $basedatos, $paciente, $ingreso, $bandera);				// Como ya se borro el egreso se comentan las siguientes líneas				// Cosulto si el paciente ya está registrado en la tabla 33 de Movhos				// $q2 = "	SELECT Historia_clinica 						// FROM ".$basedatos."_000033 						// WHERE Historia_clinica = '".$paciente."' 						// AND Num_ingreso = '".$ingreso."'						// AND Servicio = '".$codcco."' ";				// $res2 = mysql_query($q2, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q2 . " - " . mysql_error());				// $num2 = mysql_num_rows($res2);				// if($num2>0) 				// {					// $q = "	DELETE							// FROM ".$basedatos."_000033 							// WHERE Historia_clinica = '".$paciente."' 							// AND Num_ingreso = '".$ingreso."'							// AND Servicio = '".$codcco."' ";					// $res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());										// $num_affect = mysql_affected_rows();					// if($num_affect>0)					// {						// //Guardo LOG de borrado en tabla Movhos 33 						// $q = "	INSERT INTO log_agenda 												  // (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 										   // VALUES												  // ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla movhos_000033', '".$seguridad."', '".$bandera."')";						// $resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());					// }				// }			}		}	  }	}}// Establece el estado de alta para un paciente en urgenciasfunction altaPacienteUrgencias($basedatos,$basedatoshce,$cco,$servicio,$paciente,$ingreso,$wemp_pmla,$wserviciourl,$seguridad,$bandera){	global $conex; 	global $conexUnix; 	global $wbasedato;	global $wprounificado;    $wbasedato = $basedatos;		$fecha = date("Y-m-d");	$hora = date("H:i:s");	// Se consulta si el paciente sigue activo en Unix	$qact = "SELECT COUNT(*)			   FROM inpac			  WHERE pachis = '".$paciente."'				AND pacnum = ".$ingreso."";	$rs_act = odbc_do($conexUnix,$qact);	odbc_fetch_row($rs_act);	$campos = odbc_result($rs_act,1);			// Si no está activo en Unix según inpac	// mira en inpaci si está inactivo con el mismo ingreso	// Sino el ingreso fue cancelado y se borra ingreso en Matrix	if(!$campos || $campos==0)	{		// Se consulta si el paciente tiene el mismo ingreso en inpaci (Pacientes inactivos)		$qin = "SELECT COUNT(*)				   FROM inpaci				  WHERE pachis = '".$paciente."'					AND pacnum = ".$ingreso."";		$rs_in = odbc_do($conexUnix,$qin);		odbc_fetch_row($rs_in);		$fields = odbc_result($rs_in,1);	}	// Si se identificó ingreso diferente al que traemos en inpaci	// El alta debe ser con borrado de ingreso en Matrix	if(isset($fields) && $fields==0 && $bandera!="Ingreso")	{		// Consulto el ingreso actual en inpaci		$qcomp = " SELECT pacing				     FROM inpaci				    WHERE pachis = '".$paciente."'";		$rs_comp = odbc_do($conexUnix,$qcomp);		odbc_fetch_row($rs_comp);		$ing_act = odbc_result($rs_comp,1);				if($ingreso>$ing_act)			$tipo_alta = "borrado";		else			$tipo_alta = "normal";	}	else	{		// Si el alta es automática y se encontraron registros en inpac no se da de alta		// El que este en inpac y no en urgencias quiere decir que fue trasladado a otro centro de costos		// Notese que si el alta no es automatica deja dar de alta asi este activo en inpac		// El operador puede volver a activar el paciente si se equivocó al dar de alta		if(isset($bandera) && $bandera=="auto" && isset($campos) && $campos>0)			$tipo_alta = "noalta";		else			$tipo_alta = "normal";	}	// 2012-01-19	// Si fecha de egreso en unix es igual a la fecha actual y el paciente aún aparece en la tabla de 	// Estado de habitaciones (movhos_000020) con cama asignada, entonces la historia no se debe dar de alta	// Esto porque normalmente el paciente es egresado y el registro de egreso en Unix se hace al siguiente día	// Si se egresa el mismo día en matrix puede haber inconsistencias en cuanto a la ocupación de camas 	$qegr = "SELECT egregr 			   FROM inmegr			  WHERE egrhis = '".$paciente."'				AND egrnum = ".$ingreso."";	$rs_egr = odbc_do($conexUnix,$qegr);	odbc_fetch_row($rs_egr);	$fecha_egreso = odbc_result($rs_egr,1);		if($fecha_egreso == $fecha)	{		//Consulto si el paciente aún tiene cama asignada		$qhab = "	SELECT Habhis, Habing					FROM ".$basedatos."_000020					WHERE Habhis = '".$paciente."' 					AND Habing = '".$ingreso."'";		$reshab = mysql_query($qhab, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qhab . " - " . mysql_error());		$numhab = mysql_num_rows($reshab);		// Si tiene cama asiganada y fecha de egreso en unix es igual a la fecha actual		if($numhab>0)			$tipo_alta = "noalta";	}	// Consulto si el paciente está en la tabla de Historias No Automaticas	// Si está en esta tabla indica que no se puede dar de alta automáticamente	$qhna = "	SELECT Hnahis, Hnaing				FROM ".$basedatos."_000140				WHERE Hnahis = '".$paciente."'				  AND Hnaing = '".$ingreso."'				  AND Hnaest = 'on'";	$reshna = mysql_query($qhna, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qhna . " - " . mysql_error());	$numhna = mysql_num_rows($reshna);	// Si está en tabla de Historia no automática y el alta es automática	if($numhna>0 && isset($bandera) && $bandera=="auto")		$tipo_alta = "noalta";        // 2018-03-23 Se desactiva porque el usuario debe dar de Alta aunque    // tenga solo admisión.	if (strpos($wprounificado,$wserviciourl) !== false){		//Consulto si el paciente fue dado de alta por el médico en hce		$qAlta = "	SELECT Mtrhis, Mtring, Mtrcon					FROM ".$basedatoshce."_000022							WHERE Mtrhis = '".$paciente."' 							AND Mtring = '".$ingreso."'							AND Mtrest = 'on'";		$resAlta = mysql_query($qAlta, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qAlta . " - " . mysql_error());		$numAlta = mysql_num_rows($resAlta);				if($numAlta>1){           $qres = " SELECT Mtrhis, Mtring, Mtrcon				  FROM ".$basedatoshce."_000022							WHERE Mtrhis = '".$paciente."' 							AND Mtring = '".$ingreso."'							AND Mtrcon = ''							AND Mtrest = 'on' ";		   $result = mysql_query($qres, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qres . " - " . mysql_error());                      $numres = mysql_num_rows($result);           if($numres >= 1)		      $tipo_alta = "noalta";		}       }	// Inicia el proceso de alta después de validar si realmente se va a dar de alta	if($tipo_alta != "noalta")	{		if($tipo_alta == "normal")		{			//Consulto si el paciente tiene conducta asignada			$qcon = "	SELECT Mtrhis, Mtring, Mtrcon						FROM ".$basedatoshce."_000022						WHERE Mtrhis = '".$paciente."' 						AND Mtring = '".$ingreso."'						AND Mtrcon != '' 						AND Mtrcon != 'NO APLICA' 						AND Mtrest = 'on' ";			$rescon = mysql_query($qcon, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qcon . " - " . mysql_error());			$rowcon = mysql_fetch_array($rescon);			$numcon = mysql_num_rows($rescon);						// Si tiene conducta asiganada y es alta automática no se da de alta			if($numcon > 0 && isset($bandera) && $bandera=="auto") {				return "no-alta";			} else {				//Consulto si el paciente está en proceso de traslado				$qptr = "	SELECT Ubihis, Ubiing, Ubiptr, Eyrsor, Eyrsde, Cconom							FROM ".$basedatos."_000017, ".$basedatos."_000018, ".$basedatos."_000011							WHERE Ubihis = '".$paciente."' 							AND Ubiing = '".$ingreso."'							AND Ubiptr = 'on'							AND Ubihis = Eyrhis							AND Ubiing = Eyring							AND Eyrest = 'on'							AND Eyrsde = Ccocod ";				$resptr = mysql_query($qptr, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qptr . " - " . mysql_error());				$rowptr = mysql_fetch_array($resptr);				$numptr = mysql_num_rows($resptr);								// Si está en proceso de traslado no se puede dar de alta				if($numptr>0) {					return "El paciente no se puede dar de alta debido a que está en proceso de traslado para el servicio ".$rowptr['Cconom'];				} else {									//Consulto datos en tabla 18 de movhos					$qubi = "	SELECT Fecha_data, Hora_data, Ubisac								FROM ".$basedatos."_000018								WHERE Ubihis = '".$paciente."' 								AND Ubiing = '".$ingreso."'";					$resubi = mysql_query($qubi, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qubi . " - " . mysql_error());					$rowubi = mysql_fetch_array($resubi);					$numubi = mysql_num_rows($resubi);					//Actualizo tabla 18 de Movhos asignandole los parametros del alta					$q = "	UPDATE ".$basedatos."_000018 							SET Ubiald='on', Ubifad='".$fecha."', Ubihad='".$hora."', Ubiuad='".$seguridad."' 							WHERE Ubihis = '".$paciente."'							AND