ce."_000022 h22				Left JOIN ".$basedatoshce."_000035 h35				        ON  h22.Mtrcon = h35.Concod				Where  Mtrhis = '".$paciente."' 				  And  Mtring = '".$ingreso."' 				  And (Mtrmed = ''  or Mtrmed = 'NO APLICA' or Mtrcon ='') ";		$resPen = mysql_query($qPen, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qPen . " - " . mysql_error());	$numPen = mysql_num_rows($resPen);	if ( $numPen > 0 ){    	 $arrResult["error"]   = true;         $arrResult["message"] = "El paciente ya se encuentra Redireccionado";    }    // En caso de que no exista error se crear registro en hce_000022    if ( $arrResult["error"] !== true ){         // Crear registro para reasignar el paciente a otro médico. Tabla hce_000022	     $q = "INSERT INTO ".$basedatoshce."_000022 						(Medico,Fecha_data,Hora_data,Mtrhis,Mtring,Mtrcci,Mtrmed,Mtrest,Mtrtra,Mtretr,Mtrcur,Seguridad)					VALUES 						('HCE','".$fecha."','".$hora."','".$paciente."','".$ingreso."','".$cco."','','on','off','','off','C-".$seguridad."')";					 $res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());		 $num_affect = mysql_affected_rows();				 if( $num_affect>0 ){			//Guardo LOG de garabación en tabla hce_000022			$q = "	INSERT INTO log_agenda 									  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 							   VALUES									  ('".$fecha."', '".$hora."', '".$paciente."', '".$ingreso."', 'Grabacion tabla hce_000022', '".$seguridad."', 'Auto')";			$resl1 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());		 }		 $arrResult["message"] = 'El paciente ha sido redireccionado';    }    echo json_encode($arrResult);    return;}// Borra los registros del paciente para que su ingreso pueda ser reasignadofunction reasignarPacienteUrgencias($basedatos,$basedatoshce,$cco,$paciente,$ingreso,$wemp_pmla,$seguridad){	global $conex; 		// Cos