HERE Ubihis = '".$paciente."'					AND	Ubiing = '".$ingreso."'";			$res2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			$num_affect = mysql_affected_rows();			if($num_affect>0)			{				//Guardo LOG de borrado en tabla Movhos 18 				$q = "	INSERT INTO log_agenda 										  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 								   VALUES										  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla movhos_000018', '".$seguridad."', 'Reasignacion paciente')";				$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			}			// Borra registro de la tabla 37 de Root			$q = "	DELETE FROM	root_000037 					WHERE Orihis = '".$paciente."'					AND	Oriing = '".$ingreso."' 					AND	Oriori = '".$wemp_pmla."'";			$res2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			$num_affect = mysql_affected_rows();			if($num_affect>0)			{				//Guardo LOG de borrado en tabla Root 37 				$q = "	INSERT INTO log_agenda 										  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 								   VALUES										  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla root_000037', '".$seguridad."', 'Reasignacion paciente')";				$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			}			// Borra registro de egreso en la tabla 33 de Movhos			$q = "	DELETE FROM	".$basedatos."_000033 					WHERE Historia_clinica = '".$paciente."'					AND	Num_ingreso = '".$ingreso."'";			$res2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			$num_affect = mysql_affected_rows();			if($num_affect>0)			{				//Guardo LOG de borrado en tabla Movhos 33 				$q = "	INSERT INTO log_agenda 										  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 								   VALUES										  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla movhos_000033', '".$seguridad."', 'Reasignacion paciente')";				$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			}			// Borra registro en tabla 22 de Hce			$q = "	DELETE FROM ".$basedatoshce."_000022 					WHERE Mtrhis = '".$paciente."'					AND	Mtring = '".$ingreso."'";			$res = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());						$num_affect = mysql_affected_rows();			if($num_affect>0)			{				//Guardo LOG de borrado en tabla Hce 22 				$q = "	INSERT INTO log_agenda 										  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera) 								   VALUES										  ('".$fechaLog."', '".$horaLog."', '".$paciente."', '".$ingreso."', 'Borrado tabla hce_000022', '".$seguridad."', 'Reasignacion paciente')";				$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			}						if($res1)				return "ok";			else				return "Ocurrió un error en el proceso. \n Error: ".$res1;		}	} 	else			{		return "no-reasignar";	}}/********************************************************************************* CONSULTA EN UNIX LOS PACIENTES QUE NO ESTAN EN URGENCIAS Y LOS DA DE ALTA		**********************************************************************************/function actualizarAltaPacientesUnix($basedatos,$basedatoshce,$cco,$servicio,$wemp_pmla,$seguridad,$listados){	global $conex;	global $conexUnix;			$ayer = date("Y-m-d",time()-86400);	// Se consultan los pacientes activos en Unix	$qact = "SELECT pachis, pacnum			   FROM inpac			  WHERE pacser = '".$servicio."' ";		//	   , insercco,outer inemp			// 2012-01-24		//	    AND serccoser = pacser			// 2012-01-24		//		AND paccer = empcod";			// 2013-01-24		//  	AND pacfec >= '".$ayer."'";		// 2011-10-27	$rs_act = odbc_do($conexUnix,$qact);	$k = 0;	$listados_unix = array();	// Se asigna la lista de pacientes de urgencias en Unix	while (odbc_fetch_row($rs_act))	{		$listados_unix[$k] = odbc_result($rs_act,1)."-".odbc_result($rs_act,2);		$k++;	}	// Se obtiene los registros que estan en lista de pacientes activos en el programa	// pero ya no están como pacientes de urgencias en Unix	$altas_unix = array_diff($listados,$listados_unix);	$conting=0;	// Se da de alta los pacientes que no estan en urgencias de Unix	foreach ($altas_unix as $j => $value)	{		if(isset($altas_unix[$j]) && $altas_unix[$j]!="")		{			$paciente_alta = explode("-",$altas_unix[$j]);			$historia_paciente = $paciente_alta[0];			$ingreso_paciente = $paciente_alta[1];;			//echo "<br>ALTA: ".$altas_unix[$j];			altaPacienteUrgencias($basedatos,$basedatoshce,$cco,$servicio,$historia_paciente,$ingreso_paciente,$wemp_pmla,"Movhos","auto");			$conting++;		}	}	}/********************************************************************************************* CONSULTA EN UNIX LOS PACIENTES EN URGENCIAS Y ACTUALIZA EL LISTADO DE PACIENTES ACTIVOS 	*********************************************************************************************/function actualizarPacientesUnix($basedatos,$basedatoshce,$cco,$servicio,$wemp_pmla,$seguridad,$listados){	global $conex; 	global $conexUnix;		if($conexUnix)	{				// Se llama a la funcion para dar de alta a los pacientes que no están en urgencias de Unix		actualizarAltaPacientesUnix($basedatos,$basedatoshce,$cco,$servicio,$wemp_pmla,$seguridad,$listados);				$ayer = date("Y-m-d",time()-86400);		// Se consultan los pacientes activos en urgencias de Unix		$qact = "SELECT pachis, pacnum, pacced, pactid				   FROM inpac				  WHERE pacser = '".$servicio."' ";			//	   , insercco,outer inemp			// 2012-01-24			//	    AND serccoser = pacser			// 2012-01-24			//	    AND paccer = empcod";			// 2012-01-24			//		AND pacfec >= '".$ayer."'";		// 2011-10-27		$rs_act = odbc_do($conexUnix,$qact);		$conting = 0;		// Ciclo para actualizar el listado de pacientes activos en urgencias		while (odbc_fetch_row($rs_act))		{			$historia_paciente = odbc_result($rs_act,1);			$ingreso_paciente = odbc_result($rs_act,2);			$paciente_unix = $historia_paciente."-".$ingreso_paciente;						// Si la historia clínica obtenida de Unix no está en listado de pacientes, ingrésala.			if (!in_array($paciente_unix,$listados))			{				//echo "<br>INGRESO: ".$paciente_unix;				ingresarPacientesUrgencias($basedatos,$basedatoshce,$cco,$historia_paciente,$servicio,$wemp_pmla,$seguridad);				$conting++;			}		}	}}/*********************************************************************************** LISTADO DE PACIENTES INGRESADOS  *******************************************************************************************/				function agendaPacientesUrgencias($basedatos,$basedatoshce,$basedatoscliame,$cco,$wemp_pmla,$wserviciourl,$seguridad,$servicio,$wesp, $wcit, $wservicio){// Validación de usuarioif (!isset($user)){	if (!isset($_SESSION['user'])) 	{		session_register("user");	}	$user="";}//Codigo de usuario que ingreso al sistemaif (strpos($user, "-") > 0)	$wuser = substr($user, (strpos($user, "-") + 1), strlen($user));else	$wuser = "";$usuario = new Usuario();$usuario->codigo = $wuser;//Variable para determinar la empresaif(!isset($wemp_pmla)){	terminarEjecucion($MSJ_ERROR_FALTA_PARAMETRO."wemp_pmla");}//Valida codigo de usuario en sesion si no esta registrado el sistema termina la ejecucionif (!isset($_SESSION['user']) || !isset($seguridad) || $seguridad==""){	terminarEjecucion("<div align='center'>Usuario no autenticado.  Por favor cierre esta ventana y vuelva a ingresar a Matrix.</div>");} else {	global $conex; 		global $wprounificado;	global $wformularios;	// Consulta de pacientes activo