o "<td colspan=8><p align='center'>No se encontraron pacientes dados de alta en los &uacute;ltimos 2 d&iacute;as</p></td>";		echo "</tr>";	}	echo "</table>";	  }		}//ANTES DE INSERTAR UNA ALTA O UNA MUERTE PARA UN PACIENTE SE CONSULTA SI YA TUVO ALTA O MUERTE Y SE ELIMINANfunction BorrarAltasMuertesAntesDeAgregarNueva($conex, $wbasedato, $whis, $wing, $bandera){	$user_session = explode('-',$_SESSION['user']);	$seguridad = $user_session[1];	$q = "	SELECT *			FROM ".$wbasedato."_000033			WHERE Historia_clinica = '".$whis."'			AND Num_ingreso = '".$wing."'			AND Tipo_egre_serv REGEXP 'MUERTE MAYOR A 48 HORAS|MUERTE MENOR A 48 HORAS|ALTA' ";	$res = mysql_query($q,$conex) or die ("Error: ".mysql_errno()." - en el query: ".$q." - ".mysql_error());	$num = mysql_num_rows($res);	$arregloDatos = array();	if ($num > 0) {		while($row = mysql_fetch_assoc($res))		{			$result = array();			$result['fecha'] = $row['Fecha_data'];			$result['cco'] = $row['Servicio'];			$result['egreso'] = $row['Tipo_egre_serv'];			array_push( $arregloDatos, $result );		}	}	if( count( $arregloDatos )  > 0 ) {		foreach( $arregloDatos as $dato )		{			$wfecha = $dato['fecha'];			$wcco = $dato['cco'];			$wtipoEgresoABorrar = $dato['egreso'];			$q = " SELECT * "				."   FROM ".$wbasedato."_000038 "				."  WHERE Fecha_data = '".$wfecha."'"				."    AND Cieser = '".$wcco."'";			$res = mysql_query($q,$conex) or die ("Error: ".mysql_errno()." - en el query: ".$q." - ".mysql_error());			$num = mysql_num_rows($res);			$row = mysql_fetch_assoc($res);			$existe_en_la_67 = false;			$q67 = " SELECT * "				."   FROM ".$wbasedato."_000067 "				."  WHERE Fecha_data = '".$wfecha."'"				."    AND Habhis = '".$whis."'"				."    AND Habing = '".$wing."'";			$res67 = mysql_query($q67,$conex) or die ("Error: ".mysql_errno()." - en el query: ".$q." - ".mysql_error());			$num67 = mysql_num_rows($res67);			if( $num67 > 0 ){				$existe_en_la_67 = true;			}			$cant_egresos = $row['Cieegr'];			$cant_camas_ocupadas = $row['Cieocu'];			$cant_camas_disponibles = $row['Ciedis'];			$muerteMayor = $row['Ciemmay'];			$muerteMenor = $row['Ciemmen'];			$egresosAlta = $row['Cieeal'];			//Restamos uno al motivo de egreso que tenia el paciente			if(preg_match('/ALTA/i',$wtipoEgresoABorrar)) {				$egresosAlta--;				$cant_egresos--;				if( $existe_en_la_67 === false ) {					$cant_camas_ocupadas++;					$cant_camas_disponibles--;				}			} else if(preg_match('/MAYOR/i',$wtipoEgresoABorrar)) {				//Muerte mayor				$muerteMayor--;				$cant_egresos--;				if( $existe_en_la_67 === false ) {					$cant_camas_ocupadas++;					$cant_camas_disponibles--;				}			} else if(preg_match('/MENOR/i',$wtipoEgresoABorrar)) { 				// Muerte menor				$muerteMenor--;				$cant_egresos--;				if( $existe_en_la_67 === false ) {					$cant_camas_ocupadas++;					$cant_camas_disponibles--;				}			}			$query_para_log = "	SELECT *				FROM ".$wbasedato."_000033				WHERE Historia_clinica = '".$whis."'				AND Num_ingreso = '".$wing."'				AND Tipo_egre_serv = '".$wtipoEgresoABorrar."'";			$registrosFila = obtenerRegistrosFila($query_para_log);			$q ="	DELETE FROM ".$wbasedato."_000033					 WHERE Historia_clinica = '".$whis."'					   AND Num_ingreso = '".$wing."'					   AND Tipo_egre_serv = '".$wtipoEgresoABorrar."'";			$res = mysql_query($q,$conex);			$num_affect = mysql_affected_rows();						if($num_affect>0) {				$q = " UPDATE ".$wbasedato."_000038 "					."    SET Ciemmay = '".$muerteMayor."',"					."  	  Ciemmen = '".$muerteMenor."',"					."  	  Cieeal = '".$egresosAlta."',"					."  	  Cieegr = '".$cant_egresos."',"					."  	  Cieocu = '".$cant_camas_ocupadas."',"					."  	  Ciedis = '".$cant_camas_disponibles."'"					."  WHERE Fecha_data = '".$wfecha."'"					."    AND Cieser = '".$wcco."'"					."  LIMIT 1 ";				$res = mysql_query($q,$conex) or die ("Error: ".mysql_errno()." - en el query: ".$q." - ".mysql_error());				//Guardo LOG de borrado en tabla movhos 33 - Activacion paciente				$q = "	INSERT INTO log_agenda										  (Fecha, Hora, Historia, Ingreso, Accion, Seguridad, Bandera, Registros)								   VALUES										  ('".date('Y-m-d')."', '".date('H:i:s')."', '".$whis."', '".$wing."', 'Borrado tabla movhos_000033', '".$seguridad."', '".$bandera."', '".$registrosFila."')";				$resl2 = mysql_query($q, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $q . " - " . mysql_error());			}		}	}}// Retorna el código del centro de costo en Matrixfunction obtenerCcoMatrix($basedatos,$codCco,$wemp_pmla,$seguridad){	global $conex;	$cco = "";	$conex = obtenerConexionBD("matrix");	// Consulto si hay un código asociado en la tabla 11 de movhos	$qcco = "	SELECT Ccocod				  FROM ".$basedatos."_000011				 WHERE Ccoseu  = '".$codCco."'				   AND Ccoing = 'on'				   AND Ccoest = 'on'";	$rescco = mysql_query($qcco, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qcco . " - " . mysql_error());	$numcco = mysql_num_rows($rescco);		if($numcco>0) {		$rowcco = mysql_fetch_array($rescco);		$cco = $rowcco['Ccocod'];	}	return $cco;}// Actualiza los datos de los pacientes en clínicafunction actualizarDatosPacientes($basedatos,$wemp_pmla,$seguridad,$listados){	global $conex;	for($i=0;$i<count($listados);$i++)	{		$paciente = new pacienteDTO();		// Obtengo historia clínica e ingreso de paciente		$datos_paciente = explode("-",$listados[$i]);		$whistoria = $datos_paciente[0];		$wingreso = $datos_paciente[1];		$paciente->historiaClinica  = $whistoria;		$pacienteUnix = consultarPacienteUnix($paciente);  //Paciente Unix		if(isset($pacienteUnix->nombre1) && isset($pacienteUnix->ingresoHistoriaClinica)) {			// Consulta si existe el paciente en las tablas root_000036, root_000037			// Con base en histora y último ingreso			$pacienteMatrix = consultarInfoPacientePorHistoria($conex, $paciente->historiaClinica,$wemp_pmla);			$pacienteEnTablaUnica = false;	// Indica si tipo identificacion e identificación existen en tabla root_000036			$pacienteEnTablaIngresos = false;	// Indica si historia y origen existen en tabla root_000037			// En tabla root_000037 borro historia asociada en matrix a la cédula de unix			// siempre y cuando esta historia sea diferente a la asociada en Unix			borrarHistoriaDiferenteUnix($pacienteUnix, $wemp_pmla, $seguridad);			if(isset($pacienteMatrix->documentoIdentidad)) {				// Se comenta porque no es necesario usar las funciones que usan esta variable				// 2011-11-29				//if($pacienteMatrix->documentoIdentidad != $pacienteUnix->documentoIdentidad || $pacienteMatrix->tipoDocumentoIdentidad != //$pacienteUnix->tipoDocumentoIdentidad)				//{					//$mismoDocumentoIdentidad = false;				//}			} else {				$pacienteMatrix->historiaClinica = $whistoria;				$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;				$pacienteMatrix->documentoIdentidad = $pacienteUnix->documentoIdentidad;				$pacienteMatrix->tipoDocumentoIdentidad = $pacienteUnix->tipoDocumentoIdentidad;			}			// Consulto por historia y origen si existe en tabla root_000037			$pacienteEnTablaIngresos = existeEnTablaIngresos($pacienteUnix, $wemp_pmla);			// Consulto por tipo identificacion e identificación si existe en tabla root_000036			$pacienteEnTablaUnica = existeEnTablaUnicaPacientes($pacienteUnix);			//Ingreso de datos en tabla 36 de root			if(!$pacienteEnTablaUnica) {				insertarPacienteTablaUnica($pacienteUnix,$seguridad);			} else {				actualizarDatosPacienteTablaUnica($pacienteMatrix,$pacienteUnix);			}			//Ingreso de datos en tabla 37 de root			if(!$pacienteEnTablaIngresos) {				insertarIngresoPaciente($pacienteUnix, $wemp_pmla, $seguridad);			} else {				actualizarIngresoPaciente($pacienteMatrix, $pacienteUnix, $wemp_pmla);			}		}	}}// Ingresa un paciente a la lista de pacientes en urgenciasfunction ingresarPacientesUrgencias($basedatos,$basedatoshce,$cco,$whistoria,$servicio,$wemp_pmla,$seguridad){	global $conex; 		$institucion = consultarInstitucionPorCodigo($conex, $wemp_pmla);	$winstitucion = $institucion->nombre;	// Grabación de ingreso de paciente	$conex = obtenerConexionBD("matrix");	$paciente = new pacienteDTO();	$paciente->historiaClinica = $whistoria;	$pacienteUnix = consultarPacienteUnix($paciente,$servicio);  //Paciente Unix					if(isset($pacienteUnix->nombre1) && isset($pacienteUnix->ingresoHistoriaClinica)) {		// Consulta si existe el paciente en las tablas root_000036, root_000037 y movhos_000018		// Con base en histora e ingreso		$pacienteMatrix = consultarInfoPacientePorHistoria($conex, $paciente->historiaClinica);				$ingresoAnterior = "";				if(!$pacienteMatrix || !isset($pacienteMatrix->historiaClinica)) {			$pacienteMatrix = $pacienteUnix;		} else {			if(isset($pacienteMatrix->ingresoHistoriaClinica)) {				$ingresoAnterior = $pacienteMatrix->ingresoHistoriaClinica;			} else {				$pacienteMatrix->ingresoHistoriaClinica = $ingresoAnterior; 			}						if($pacienteUnix) {				// Si el ingreso encontrado en Matrix (movhos_000018) es menor 				if($pacienteMatrix->ingresoHistoriaClinica=="") {					$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;									} elseif($pacienteMatrix->ingresoHistoriaClinica < $pacienteUnix->ingresoHistoriaClinica) {										if(isset($pacienteMatrix->historiaClinica) && !empty($pacienteMatrix->historiaClinica))					{						// Consulto si el ingreso no tiene alta definitiva						$qalt = "	SELECT Ubiald   									FROM ".$basedatos."_000018 									WHERE Ubihis  = '".$pacienteMatrix->historiaClinica."' 									AND	  Ubiing = '".$pacienteMatrix->ingresoHistoriaClinica."'									AND	  Ubiald != 'on'";						$resalt = mysql_query($qalt, $conex) or die ("Error: " . mysql_errno() . " - en el query: " . $qalt . " - " . mysql_error());						$numalt = mysql_num_rows($resalt);						// Define si le doy de alta o no 1=no tiene alta 0=tiene alta						// Si no tiene alta definitiva le doy de alta						if($numalt>0)							@altaPacienteUrgencias($basedatos,$basedatoshce,$cco,$servicio,$pacienteMatrix->historiaClinica,$pacienteMatrix->ingresoHistoriaClinica,$wemp_pmla,"Movhos","Ingreso");												$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;						//$pacienteMatrix->nombre1 = "";					}				} elseif((($pacienteMatrix->ingresoHistoriaClinica)*1) >= (($pacienteUnix->ingresoHistoriaClinica)*1)) {										if(isset($pacienteMatrix->historiaClinica) && !empty($pacienteMatrix->historiaClinica)) {						borraIngresosMayores($basedatos,$basedatoshce,$cco,$pacienteMatrix->historiaClinica,$pacienteUnix->ingresoHistoriaClinica,$wemp_pmla,"Movhos","BorradoIngresoMayor",$pacienteUnix->fechaIngreso,$pacienteUnix->horaIngreso);						$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;						//$pacienteMatrix->nombre1 = "";					}				}			}							}				// Si ya se encuentra admitido va a consultar la información		// La guia es la tabla 18, si esta ahi, YA SE CONSIDERA INGRESADO		if(!isset($pacienteMatrix->historiaClinica) || empty($pacienteMatrix->historiaClinica)) {			$pacienteMatrix->historiaClinica = $pacienteUnix->historiaClinica;		}				$pacienteConResponsablePaciente = false;	// Indica si historia e ingreso existen en tabla movhos_000016		// Consulto por historia e ingreso si existe en tabla movhos_000016		$pacienteConResponsablePaciente = existeEnTablaResponsables($pacienteUnix, $wemp_pmla);					//Ingreso de datos en tabla 16 de movhos		if(!$pacienteConResponsablePaciente) {			insertarResponsablePaciente($pacienteUnix, $wemp_pmla, $seguridad);		} else {			actualizarResponsablePaciente($pacienteMatrix, $pacienteUnix);		}				// Busca por historia e ingreso en la tabla movhos_000018 si existen registros		$pacienteIngresado = pacienteIngresado($pacienteMatrix);		// Busca por historia e ingreso en la tabla hce_000022 si existen registros		$pacienteIngresadoHce = pacienteIngresadoHce($pacienteMatrix);		if(!isset($pacienteMatrix->ingresoHistoriaClinica) or !$pacienteIngresado or !$pacienteIngresadoHce)		{			if(!isset($pacienteMatrix->ingresoHistoriaClinica) or !$pacienteIngresado)			{				$pacienteEnTablaUnica = false;	// Indica si tipo identificacion e identificación existen en tabla root_000036				$pacienteEnTablaIngresos = false;	// Indica si historia y origen existen en tabla root_000037								// Se comenta porque no es necesario usar las funciones que usan esta variable - 2011-11-29				//$mismoDocumentoIdentidad = true;	// Indica si paciente unix y paciente matrix tienen el mismo documento				// En tabla root_000037 borro historia asociada en matrix a la cédula de unix				// siempre y cuando esta historia sea diferente a la asociada en Unix				borrarHistoriaDiferenteUnix($pacienteUnix, $wemp_pmla, $seguridad);				// Si exite documento de identidad en matrix verifico que sea el mismo de Unix				if(isset($pacienteMatrix->documentoIdentidad)) {					// Se comenta porque no es necesario usar las funciones que usan esta variable					/* 2011-11-29					if($pacienteMatrix->documentoIdentidad != $pacienteUnix->documentoIdentidad || $pacienteMatrix->tipoDocumentoIdentidad != $pacienteUnix->tipoDocumentoIdentidad)					{						$mismoDocumentoIdentidad = false;					}					*/				} 				else {					$pacienteMatrix->historiaClinica = $whistoria;					$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;					$pacienteMatrix->documentoIdentidad = $pacienteUnix->documentoIdentidad;					$pacienteMatrix->tipoDocumentoIdentidad = $pacienteUnix->tipoDocumentoIdentidad;  				}				// Consulto por historia y origen si existe en tabla root_000037				$pacienteEnTablaIngresos = existeEnTablaIngresos($pacienteUnix, $wemp_pmla);				// Consulto por tipo identificacion e identificación si existe en tabla root_000036				$pacienteEnTablaUnica = existeEnTablaUnicaPacientes($pacienteUnix);															// Actualiza documento de identidad en tabla root_000037				// Se comenta porque la actuailzación de documento en tabla root_000037 se realiza en las siguientes funciones				/* 2011-11-29				if(!$mismoDocumentoIdentidad)					actualizarDocumentoPacienteTablaIngresos($pacienteMatrix,$pacienteUnix,$wemp_pmla);				*/				// Actualiza documento de identidad en tabla root_000036				// Se comenta porque la actuailzación de documento en tabla root_000036 se realiza en las siguientes funciones				/* 2011-11-29				if(!$mismoDocumentoIdentidad)					actualizarDocumentoPacienteTablaUnica($pacienteMatrix,$pacienteUnix);				*/								//Ingreso de datos en tabla 36 de root				if(!$pacienteEnTablaUnica) {					insertarPacienteTablaUnica($pacienteUnix,$seguridad);				} else {					actualizarDatosPacienteTablaUnica($pacienteMatrix,$pacienteUnix);				}									//Ingreso de datos en tabla 37 de root				if(!$pacienteEnTablaIngresos) {					insertarIngresoPaciente($pacienteUnix, $wemp_pmla, $seguridad);				} else {					$pacienteMatrix->ingresoHistoriaClinica = $ingresoAnterior;					actualizarIngresoPaciente($pacienteMatrix, $pacienteUnix, $wemp_pmla);					$pacienteMatrix->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;				}			}						//Proceso de movimiento hospitalario			$ingresoPaciente = new ingresoPacientesDTO();							$ingresoPaciente->historiaClinica = $pacienteUnix->historiaClinica;			$ingresoPaciente->ingresoHistoriaClinica = $pacienteUnix->ingresoHistoriaClinica;			$ingresoPaciente->servicioActual = $cco;			$ingresoPaciente->habitacionActual = "";			$ingresoPaciente->fechaIngreso = $pacienteUnix->fechaIngreso;			$ingresoPaciente->horaIngreso = $pacienteUnix->horaIngreso;						$ingresoPaciente->fechaAltaProceso = "0000-00-00";			$ingresoPaciente->horaAltaProceso = "00:00:00";			$ingresoPaciente->fechaAltaDefinitiva = "0000-00-00";			$ingresoPaciente->horaAltaDefinitiva = "00:00:00";							if(esCirugia($cco))				$ingresoPaciente->enProcesoTraslado = "on";			else				$ingresoPaciente->enProcesoTraslado = "off";			$ingresoPaciente->altaDefinitiva = "off";			$ingresoPaciente->altaEnProceso = "off";							$ingresoPaciente->usuario = "A-".$basedatos;							//Si hay un registro en urgencias			$pacienteIngresado = pacienteIngresado($pacienteMatrix);						//Grabar ingreso paciente			$ingresoPaciente->servicioAnterior = "";			$ingresoPaciente->habitacionAnterior = "";						if(!$pacienteIngresado or !$pacienteIngresadoHce) {				if(!$pacienteIngresado)					grabarIngresoPaciente($ingresoPaciente, $seguridad);								if(!$pacienteIngresadoHce)					registrarIngresoPaciente($ingresoPaciente, $seguridad);								return 'ok';			} else {				return 'existente';			}					} else {			return 'existente';		}	} else {		return 'no-urg';	}}	//2016-03-02 Verónica Arismendy se consulta el prefijo y el número de la tabla de citas dependiendo del tipo de atención se devuelve el nombre completo de la tabla de citas	function consultarPrefijoNumeroTabla($wcit, $wemp_pmla, $conex){		//Se consulta en la 51 el prefijo de la tabla envíandole la descripción		$q = "SELECT detval 		      FROM root_000050, root_000051 			  WHERE empcod = '".$wemp_pmla."' 			  AND empest = 'on' 			  AND empcod = detemp 			  AND detdes = '".$wcit."' 			  AND detapl = 'citas'		";			echo ' consulta prefijo '.$q;		$res = mysql_query($q,$conex) or die ("Error: ".mysql_errno()." - en el query: ".$q." - ".mysql_error());        $pref = mysql_fetch_assoc($res);		$prefijoTabla = isset($pref["detval"]) && $pref["detval"] != "" ? $pref["detval"] : "";				//Con el mismo parametro se hace un case para ver que tabla de citas maneja. OBS: cada que se agregue esta funcionalidad a un servcio se debe añadir al case		$numTablaCita = array();		switch($wcit){			case 'Fisioterapia' : 							$numTablaCita