include_once("conex.php");function bi($d,$n,$k){	//$n--;	if($n > 0)	{		$li=0;		$ls=$n;		while ($ls - $li > 1)		{			$lm=(integer)(($li + $ls) / 2);			$val=strncmp(strtoupper($k),strtoupper($d[$lm][0]),20);			if($val == 0)				return $lm;			elseif($val < 0)					$ls=$lm;				else					$li=$lm;		}		if(strtoupper($k) == strtoupper($d[$li][0]))			return $li;		elseif(strtoupper($k) == strtoupper($d[$ls][0]))					return $ls;				else					return -1;	}	elseif(isset($d[0][0]) and $d[0][0] == $k)			return 0;		else			return -1;}session_start();if(!isset($_SESSION['user']))echo "error";else{ 	$key = substr($user,2,strlen($user));	
	
	echo "<form action='hceidc36.php' method=post>";	if(!isset($v0) or !isset($v1))	{		echo  "<center><table border=0>";		echo "<tr><td align=center colspan=2><b>PROMOTORA MEDICA LAS AMERICAS S.A.<b></td></tr>";		echo "<tr><td colspan=2 align=center><b>REGISTRO DE ATENCIONES</b></td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Medico</td><td bgcolor=#cccccc align=center>";		$query = "SELECT CONCAT(Medap1,' ',Medap2,' ',Medno1,' ',Medno2),Meddoc,Meduma from mhosidc_000048 order by 1 ";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		if ($num>0)		{			echo "<select name='v0' onChange='enter()'>";			echo "<option>----</option>";			for ($i=0;$i<$num;$i++)			{				$row = mysql_fetch_array($err);				if(isset($wesp) and $wesp == $row[0]."-".$row[1])					echo "<option selected>".$row[0]."-".$row[1]."-".$row[2]."</option>";				else					echo "<option>".$row[0]."-".$row[1]."-".$row[2]."</option>";			}			echo "</select>";		} 		echo "</td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Fecha</td>";		echo "<td bgcolor=#cccccc align=center><input type='TEXT' name='v1' size=10 maxlength=10 value='".date("Y-m-d")."'></td></tr>";		echo "<tr><td bgcolor=#cccccc  colspan=2 align=center><input type='submit' value='ENTER'></td></tr></table>";	}	else	{		$lon = strrpos($v0,"-") - strpos($v0,"-") - 1;		$medico = substr($v0,strrpos($v0,"-")+1);		$cedula = substr($v0,strpos($v0,"-")+1,$lon);		$H36=array();		$query  = "select Firhis,Firpro,Encdes from hceidc_000036,hceidc_000001 ";		if($medico == "0800103" or $medico == "0800104")			$query .= " where firusu in ('0800103','0800104') ";		else			$query .= " where firusu= '".$medico."' ";		$query .= "   and firpro = Encpro ";		$query .= "   and hceidc_000036.fecha_data='".$v1."' ";		$query .= " order by 1 ";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		$clave = "";		$k = -1;		if($num > 0)		{			for ($i=0;$i<$num;$i++)			{				$row = mysql_fetch_array($err);				if($row[0] != $clave)				{					$k++;					$H36[$k][0] = $row[0];					$H36[$k][1] = $row[2];					$clave = $row[0];				}				else				{					$H36[$k][1] .= "<br>".$row[2];				}			}		}		$numh36=$k;		$query  = "select Methis,Pacno1,Pacno2,Pacap1,Pacap2,Pacnac,Pacsex from mhosidc_000047,root_000037,root_000036 ";		if($medico == "0800103" or $medico == "0800104")			$query .= " where Metdoc in ('42879340','8302554') ";		else			$query .= " where Metdoc = '".$cedula."' ";		if($v1 == date("Y-m-d"))			$query .= "   and Metest = 'on' ";		else			$query .= "   and Metfek = '".$v1."' ";		$query .= "   and Methis = Orihis ";		$query .= "   and Oriori = '10' ";		$query .= "   and Oritid = Pactid ";		$query .= "   and Oriced = Pacced ";		$query .= " Group by 1 ";		$query .= " order by 2,3,4,5 ";		$err = mysql_query($query,$conex) or die(mysql_errno().":".mysql_error());		$num = mysql_num_rows($err);		echo "<center><table id='tipoTABLE' CELLSPACING=0 CELLPADDING=2>";		echo "<tr><td colspan=8 id='tipo4'><b>PROMOTORA MEDICA LAS AMERICAS S.A.</b></td></tr>";		echo "<tr><td colspan=8 id='tipo4'><b>DIRECCION DE INFORMATICA</b></td></tr>";		echo "<tr><td colspan=8 id='tipo4'><b>REGISTRO DE ATENCIONES EN : ".$v1."</b></td></tr>";		echo "<tr><td colspan=8 id='tipo4'><b>POR MEDICO : ".$v0."</b></td></tr>";		echo "<tr>";		echo "<td id='tipo3'><b>Historia</b></td>";		echo "<td id='tipo3'><b>Primer<br> Nombre</b></td>";		echo "<td id='tipo3'><b>Segundo<br> Nombre</b></td>";		echo "<td id='tipo3'><b>Primer<br> Apellido</b></td>";		echo "<td id='tipo3'><b>Segundo<br> Apellido</b></td>";		echo "<td id='tipo3'><b>Fecha<br> Nacimiento</b></td>";		echo "<td id='tipo3'><b>Sexo</b></td>";		echo "<td id='tipo3'><b>Formularios<br>Diligenciados</b></td>";		echo "</tr>"; 		for ($i=0;$i<$num;$i++)		{			$row = mysql_fetch_array($err);			if($i % 2 == 0)				$tipo = "tipo1";			else				$tipo = "tipo2";			if($row[4] == "")				$row[4] = "&nbsp;";			if($row[6] == "")				$row[6] = "&nbsp;";			echo "<tr>";			echo "<td id='".$tipo."'>".$row[0]."</td>";			echo "<td id='".$tipo."'>".$row[1]."</td>";			echo "<td id='".$tipo."'>".$row[2]."</td>";			echo "<td id='".$tipo."'>".$row[3]."</td>";			echo "<td id='".$tipo."'>".$row[4]."</td>";			echo "<td id='".$tipo."'>".$row[5]."</td>";			echo "<td id='".$tipo."'>".$row[6]."</td>";			$pos=bi($H36,$numh36,$row[0]);			if($pos != -1)				$whce = $H36[$pos][1];			else				$whce = "PENDIENTE";			echo "<td id='".$tipo."'>".$whce."</td>";			echo "</tr>"; 		}		echo "</table></center>"; 	}}?></body></html>