                if($rowqueteinsumo = mysql_fetch_array($res_sql_paquete_insumo))
                {
                    $resulpaquete =  explode('-',$rowqueteinsumo['Paqdetpro']);
                    $resulpaquete =  (count($resulpaquete) >1) ? $resulpaquete[1] :$resulpaquete[0];

