                // global $arr_especialidades_liq, $wesbilateral, $numero_cirugia, $porcentajes_CX_bilaterales, $via_primera_bilateral, $via_cirugia_en_curso, $especialid_primera_bilateral, $especialidad_cirugia_en_curso;
