                        }

                        if(array_key_exists($indice_proced_concepNoCobro, $cx_queNoSeCobran))
                        {
                            $arr_porcentajes[$concepto_porcentaje]["valor_porcentaje"] = 0;
                        }
                        elseif(array_key_exists($indice_proced_concepNoCobro, $cx_queSISeCobran))
                        {
