function consultarModalidadYPuntosProcedimientos($conex, $wbasedato, $wemp_pmla, $wcentro_costo, $arr_datos_liquidar, $wtipo_emp_valida_nopos, $wprocedimiento_add, $wconcepto_add, $arr_extras, $arr_conceptos_nombres, $arr_datos_liquidados,
