	echo "<input type='HIDDEN' name= 'empresa' value='".$empresa."'>";	if(!isset($wano) or !isset($wmes) or !isset($wcco) or !isset($wtip) or (strtoupper($wtip) != "R" and strtoupper($wtip) != "D"))	{		echo "<center><table border=0>";		echo "<tr><td align=center colspan=2><b>PROMOTORA MEDICA LAS AMERICAS S.A.<b></td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Año de Proceso</td>";		echo "<td bgcolor=#cccccc align=center><input type='TEXT' name='wano' size=4 maxlength=4></td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Mes de Proceso</td>";		echo "<td bgcolor=#cccccc align=center><input type='TEXT' name='wmes' size=2 maxlength=2></td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Centro de Costos</td><td bgcolor=#cccccc align=center>";		$query = "SELECT Ccocod, Ccodes    from ".$empresa."_000003 order by Ccocod";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		if ($num>0)		{			echo "<select name='wcco'>";			echo "<option>0-TODAS LAS SEDES</option>";			for ($i=0;$i<$num;$i++)			{				$row = mysql_fetch_array($err);				echo "<option>".$row[0]."-".$row[1]."</option>";			}			echo "</select>";		}		echo "</td></tr>";		echo "<tr><td bgcolor=#cccccc align=center>Tipo (R-Resunido/D-Detallado)</td>";		echo "<td bgcolor=#cccccc align=center><input type='TEXT' name='wtip' size=1 maxlength=1></td></tr>";		echo "<tr><td bgcolor=#cccccc  colspan=2 align=center><input type='submit' value='ENTER'></td></tr></table>";	}	else	{		//                       0          1      2        3       4       5         		$query  = "SELECT mid(Artcod,1,2),Artcod, Artnom, Salexi, Salpro, Salvuc  from ".$empresa."_000001, ".$empresa."_000014 ";		$query .= "  where Artcod = Salcod ";		if(substr($wcco,0,strpos($wcco,"-")) != "0")			$query .= "   and Salcco = '".substr($wcco,0,strpos($wcco,"-"))."' ";		$query .= "   and Salano = ".$wano;		$query .= "   and Salmes = ".$wmes;		$query .= "   Order by 1,3 ";		$err = mysql_query($query,$conex);		$num = mysql_num_rows($err);		echo "<table border=0>";		echo "<tr><td colspan=6 align=center><b>PROMOTORA MEDICA LAS AMERICAS S.A.</b></td></tr>";		echo "<tr><td colspan=6 align=center><b>DIRECCION DE INFORMATICA</b></td></tr>";		echo "<tr><td colspan=6 align=center><b>REPORTE DE SALDOS X SEDE X LABORATORIO (UVGlobal)</b></td></tr>";		echo "<tr><td colspan=6 align=center><b>SEDE : ".$wcco."</b></td></tr>";		echo "<tr><td colspan=6 align=center><b>SALDOS A : ".$wano."/".$wmes."</b></td></tr>";		echo "<tr>";		echo "<td bgcolor=#cccccc><b>Codigo</b></td>";		echo "<td bgcolor=#cccccc><b>Descripcion</b></td>";		echo "<td align=right bgcolor=#cccccc><b>Existencias</b></td>";		echo "<td align=right bgcolor=#cccccc><b>Costo<br>Promedio</b></td>";		echo "<td align=right bgcolor=#cccccc><b>Costo<br>Total</b></td>";		echo "<td align=right bgcolor=#cccccc><b>Valor<BR>Ultima<BR>Compra</b></td>";		echo "</tr>"; 		$wlab="";		$tot=array();		$totG=array();		$tot[0]=0;		$tot[1]=0;		$tot[2]=0;		$tot[3]=0;		$totG[0]=0;		$totG[1]=0;		$totG[2]=0;		$totG[3]=0;		for ($i=0;$i<$num;$i++)		{			if($i % 2 == 0)				$color="#99CCFF";			else				$color="#FFFFFF";			$row = mysql_fetch_array($err);			if($wlab != $row[0])			{				if($i > 0)					echo "<tr><td colspan=2 bgcolor=#cccccc><b> TOTAL LABORATORIO : ".$wlab."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[0],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[1],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[3],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[2],2,'.',',')."</b></td></tr>";				echo "<tr><td colspan=6 bgcolor=#CC99FF><b>LABORATORIO : ".$row[0]."</b></td></tr>";				$tot[0]=0;				$tot[1]=0;				$tot[2]=0;				$tot[3]=0;				$wlab=$row[0];			}			$costoT=$row[3] * $row[4];			if($row[3] > 0)			{				$tot[0] += $row[3];				$tot[1] += $row[4];				$tot[2] += $row[5];				$tot[3] += $costoT;				$totG[0] += $row[3];				$totG[1] += $row[4];				$totG[2] += $row[5];				$totG[3] += $costoT;			}			if(strtoupper($wtip) == "D")			{				echo "<tr>";				echo "<td bgcolor=".$color." >".$row[1]."</td>";				echo "<td bgcolor=".$color." >".$row[2]."</td>";				echo "<td bgcolor=".$color." align=right>".number_format($row[3],2,'.',',')."</td>";				echo "<td bgcolor=".$color." align=right>".number_format($row[4],2,'.',',')."</td>";				echo "<td bgcolor=".$color." align=right>".number_format($costoT,2,'.',',')."</td>";				echo "<td bgcolor=".$color." align=right>".number_format($row[5],2,'.',',')."</td>";				echo "</tr>"; 			}		}		echo "<tr><td colspan=2 bgcolor=#cccccc><b> TOTAL LABORATORIO : ".$wlab."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[0],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[1],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[3],2,'.',',')."</b></td><td bgcolor=#cccccc align=right><b>".number_format($tot[2],2,'.',',')."</b></td></tr>";		echo "<tr><td colspan=2 bgcolor=#999999><b> TOTAL GENERAL : </b></td><td bgcolor=#999999 align=right><b>".number_format($totG[0],2,'.',',')."</b></td><td bgcolor=#999999 align=right><b>".number_format($totG[1],2,'.',',')."</b></td><td bgcolor=#999999 align=right><b>".number_format($totG[3],2,'.',',')."</b></td><td bgcolor=#999999 align=right><b>".number_format($totG[2],2,'.',',')."</b></td></tr>";		echo "</table>"; 	}}?></body></html>